import React, { useContext, useState } from 'react';
import { TodoContext } from '../context/TodoContext';
import { Table, Button, Form } from 'react-bootstrap';

const TodoList = () => {
  const { users, todos, changeStatus } = useContext(TodoContext);
  const [filterUser, setFilterUser] = useState('');
  const [filterCompleted, setFilterCompleted] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  const handleSort = () => {
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const filteredTodos = todos.filter(todo => {
    return (filterUser === '' || todo.userId === parseInt(filterUser)) &&
           (filterCompleted === '' || todo.completed === (filterCompleted === 'true'));
  }).sort((a, b) => {
    if (sortOrder === 'asc') {
      return a.title.localeCompare(b.title);
    } else {
      return b.title.localeCompare(a.title);
    }
  });

  return (
    <div className="container">
      <div className="row">
        <div className="col-9">
          <Button onClick={handleSort}>Sort: {sortOrder === 'asc' ? 'Ascending' : 'Descending'} by Title</Button>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>No.</th>
                <th>Title</th>
                <th>User</th>
                <th>Completed</th>
                <th>Change Status</th>
              </tr>
            </thead>
            <tbody>
              {filteredTodos.map(todo => (
                <tr key={todo.id}>
                  <td>{todo.id}</td>
                  <td>{todo.title}</td>
                  <td>{users.find(user => user.id === todo.userId)?.name}</td>
                  <td>{todo.completed ? 'Finished' : 'Unfinished'}</td>
                  <td><Button onClick={() => changeStatus(todo.id)}>Change</Button></td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
        <div className="col-3">
          <h5>Users</h5>
          {users.map(user => (
            <Form.Check
              key={user.id}
              type="radio"
              label={user.name}
              name="userFilter"
              value={user.id}
              onChange={(e) => setFilterUser(e.target.value)}
            />
          ))}
          <h5>Completed</h5>
          <Form.Check
            type="radio"
            label="All"
            name="completedFilter"
            value=""
            onChange={(e) => setFilterCompleted(e.target.value)}
          />
          <Form.Check
            type="radio"
            label="Finished"
            name="completedFilter"
            value="true"
            onChange={(e) => setFilterCompleted(e.target.value)}
          />
          <Form.Check
            type="radio"
            label="Unfinished"
            name="completedFilter"
            value="false"
            onChange={(e) => setFilterCompleted(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default TodoList;
