import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const TodoContext = createContext();

export const TodoProvider = ({ children }) => {
  const [users, setUsers] = useState([]);
  const [todos, setTodos] = useState([]);
  const [feedback, setFeedback] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:9999/user').then(res => setUsers(res.data));
    axios.get('http://localhost:9999/todo').then(res => setTodos(res.data));
  }, []);

  const changeStatus = (id) => {
    const todo = todos.find(todo => todo.id === id);
    todo.completed = !todo.completed;
    axios.put(`http://localhost:9999/todo/${id}`, todo).then(() => {
      setTodos(todos.map(t => t.id === id ? todo : t));
      setFeedback(`Change success for todo id: ${id}`);
    });
  };

  const clearFeedback = () => setFeedback(null);

  return (
    <TodoContext.Provider value={{ users, todos, changeStatus, feedback, clearFeedback }}>
      {children}
    </TodoContext.Provider>
  );
};
