import React, { useContext, useState, useEffect } from 'react';
import { TodoContext } from '../context/TodoContext';
import { Table, Button, Form, Modal } from 'react-bootstrap';

const TodoList2 = () => {
  const { users, todos, changeStatus, feedback, clearFeedback } = useContext(TodoContext);
  const [filterUsers, setFilterUsers] = useState([]);
  const [filterCompleted, setFilterCompleted] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    // Tích tất cả các checkbox khi component được mount
    setFilterUsers(users.map(user => user.id.toString()));
  }, [users]);

  const handleSort = () => {
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const handleUserFilterChange = (userId) => {
    setFilterUsers((prevFilterUsers) => {
      if (prevFilterUsers.includes(userId)) {
        return prevFilterUsers.filter((id) => id !== userId);
      } else {
        return [...prevFilterUsers, userId];
      }
    });
  };

  const filteredTodos = todos.filter(todo => {
    const userFilter = filterUsers.length === 0 || filterUsers.includes(todo.userId.toString());
    const completedFilter = filterCompleted === '' || todo.completed === (filterCompleted === 'true');
    return userFilter && completedFilter;
  }).sort((a, b) => {
    if (sortOrder === 'asc') {
      return a.title.localeCompare(b.title);
    } else {
      return b.title.localeCompare(a.title);
    }
  });

  return (
    <div className="container">
      <div className="row">
        <div className="col-9">
          {feedback && (
            <Modal show={feedback !== null} onHide={clearFeedback}>
              <Modal.Header closeButton>
                <Modal.Title>Feedback</Modal.Title>
              </Modal.Header>
              <Modal.Body>{feedback}</Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={clearFeedback}>
                  Close
                </Button>
              </Modal.Footer>
            </Modal>
          )}
          <Button onClick={handleSort}>
            Sort: {sortOrder === 'asc' ? 'Ascending' : 'Descending'} by Title
          </Button>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>No.</th>
                <th>Title</th>
                <th>User</th>
                <th>Completed</th>
                <th>Change Status</th>
              </tr>
            </thead>
            <tbody>
              {filteredTodos.map(todo => {
                const user = users.find(user => user.id == todo.userId);
                return (
                  <tr key={todo.id}>
                    <td>{todo.id}</td>
                    <td>{todo.title}</td>
                    <td>{user.name}</td>
                    <td>{todo.completed ? 'Finished' : 'Unfinished'}</td>
                    <td><Button onClick={() => changeStatus(todo.id)}>Change</Button></td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
        </div>
        <div className="col-3">
          <h5>Users</h5>
          {users.map(user => (
            <Form.Check
              key={user.id}
              type="checkbox"
              label={user.name}
              value={user.id}
              checked={filterUsers.includes(user.id.toString())}
              onChange={(e) => handleUserFilterChange(e.target.value)}
            />
          ))}
          <h5>Completed</h5>
          <Form.Check
            type="radio"
            label="All"
            name="completedFilter"
            value=""
            onChange={(e) => setFilterCompleted(e.target.value)}
          />
          <Form.Check
            type="radio"
            label="Finished"
            name="completedFilter"
            value="true"
            onChange={(e) => setFilterCompleted(e.target.value)}
          />
          <Form.Check
            type="radio"
            label="Unfinished"
            name="completedFilter"
            value="false"
            onChange={(e) => setFilterCompleted(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default TodoList2;
